import React, { Component, Fragment } from 'react';

import ReactCSSTransitionGroup from 'react-addons-css-transition-group';


import IncomeReport from '../../Commerce/Examples/Components/IncomeReport';
import IncomeReport2 from '../../Commerce/Examples/Components/IncomeReport2';


import classnames from 'classnames';


import {
    faCalendarAlt,

} from '@fortawesome/free-solid-svg-icons';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import DatePicker from 'react-datepicker';



import {
    InputGroup, InputGroupAddon,
    Col, Nav,
    Button,
    CardHeader,
    NavItem,
    NavLink,
    TabContent,
    TabPane,
    Card,
    CardBody, Input, CardFooter,
    Form, FormGroup, Label, CustomInput,
    Modal, ModalHeader, ModalBody, ModalFooter

} from 'reactstrap';

import {
    ResponsiveContainer,

} from 'recharts';

import img from './LTE.jpg';

import './app.css';

import ReactTable from "react-table";

const masDat = [
    { name: 'Habib Akbar'},
    { name: 'Aulia Muslim Hidayatullah'},
    { name: 'Habib Akbar Aziz'},
    { name: 'Page D'},
    { name: 'Page E'},
    { name: 'Page F'},
    { name: 'Page G'},
    { name: 'Page C'},
    { name: 'Page D'},
    { name: 'Page E'},
    { name: 'Page F'},
    { name: 'Page G'},
]

export default class MinimalDashboard2 extends Component {

    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            startDate: new Date(),
            open : false,
            data: 1,
            d : '',
            masDat : masDat

        };

        this.tambah = this.tambah.bind(this);
        this.toggle = this.toggle.bind(this);
        this.togglePop1 = this.togglePop1.bind(this);
        this.state = {
            popoverOpen1: false,
            activeTab: '2',
        }
        this.handleChange = this.handleChange.bind(this);
       
        this.open = this.open.bind(this);
    }

   

    togglePop1() {
        this.setState({
            popoverOpen1: !this.state.popoverOpen1
        });
    }

    toggle(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }

    tambah() {
        this.setState({
            modal: !this.state.modal
        });
    }


    handleChange(date) {
        this.setState({
            startDate: date
        });
    }

    open(){
        this.setState({
             open:!this.state.open
        })
    }

    cek(value){
        this.setState({
            open: !this.state.open,
            data: value
        })
    }

    render() {

        console.log(this.state.masDat)
        const { data , d } = this.state;
        const filterData = this.state.masData ? this.state.masData.filter(
            e => e.name.toLowerCase().indexOf(d.toLowerCase()) > -1
        ) : this.state.masData;

        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                    <Card tabs="true" className="mb-3">
                        <TabContent activeTab={this.state.activeTab}>
                            <TabPane tabId="2">
                                <CardBody>
                                    <div className="widget-chart-wrapper widget-chart-wrapper-lg opacity-10 m-0">
                                        <ResponsiveContainer width="100%" height="100%">
                                            <Col sm="12" md="12" xl="12" style={{paddingBottom:'50%'}}>
                                                <FormGroup row>
                                                    <Label for="Access" sm={12} size="lg">1. <font style={{ paddingLeft:'20px' }}>New Account Owner</font></Label>
                                                    <Col sm={4} style={{ paddingLeft: '56px' }}>

                                                        <CustomInput type="select"
                                                            name="Access" onClick={this.open}>
                                                            <option value={data} selected disabled hidden>{data ? data : "Select Employee"}</option>
                                                                </CustomInput>

                                                        <div style={{ position: 'absolute', zIndex: '2', width: '453px',height:'66px',  visibility: this.state.open ? '' : 'hidden', paddingTop:'10px' , borderTopLeftRadius:'10px'}}>
                                                            <div className="form">
                                                                
                                                                    <Col sm="12" md="12" xl="12" className="col-input">
                                                                        <Input type="text" name="text" placeholder="search" className="form-input" value={d}  onChange={(e) => {this.setState({d: e.target.value}) }} />   
                                                                    </Col>
                                                               
                                                            </div>
                                                            <div className="deni">
                                                                <ResponsiveContainer width="100%" height="100%">
                                                                    <Col sm="12" md="12" xl="12" style={{ paddingLeft:'22px', visibility: this.state.open ? '' : 'hidden' , overflow:'auto' }}>

                                                                        {masDat.map(el => (
                                                                            <div style={{ display:'flex' , flexDirection:'row' }}>
                                                                             <div>
                                                                                <img src={img} height="60px" />
                                                                            </div>
                                                                                <div style={{ display: 'flex', flexDirection: 'row' }}>
                                                                                <div style={{ flexDirection: 'column' }}>
                                                                                        <b><p style={{ marginTop: '14px' }}>{el.name}</p></b>
                                                                                        <p style={{ marginTop:'-20px' }}>Scrum Master</p>
                                                                           
                                                                                </div>
                                                                            </div>
                                                                            </div>
                                                                        ))}
                                                                    </Col>
                                                                </ResponsiveContainer>
                                                            </div>
                                                        </div>

                                                    </Col>
                                                </FormGroup>
                                                <FormGroup row>
                                                    <Label for="Access" sm={12} size="lg">2. <font style={{ paddingLeft: '20px' }}>Masih pertanyaan</font></Label>
                                                    <Col sm={4} style={{ paddingLeft: '56px' }}>
                                                        <CustomInput type="select"
                                                            name="Access" />
                                                            
                                                            
                                                    </Col>
                                                </FormGroup>
                                            </Col>
                                        </ResponsiveContainer>
                                    </div>

                                </CardBody>
                                <CardFooter className="d-block p-4 text-right">
                                    <Button style={{ right: '10px', backgroundColor: "#da222200", color: "black", borderColor: "#ff000000" }} className="fsize-1" size="lg">
                                        Cancel
                                    </Button>
                                    <Button style={{ backgroundColor: '#295950' }} className="btn-wide fsize-1" onClick={this.open} size="lg">
                                        Save
                                    </Button>
                                </CardFooter>
                            </TabPane>
                            
                        </TabContent>
                    </Card>

                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}
