import React, { Component, Fragment } from 'react';


import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

import {
    Col,
    Button,
    TabContent,
    TabPane,
    Card,
    CardBody, Input, CardFooter,
    FormGroup, Label, CustomInput

} from 'reactstrap';

import {
    ResponsiveContainer,

} from 'recharts';




class MinimalDashboard4 extends Component {

    constructor(props) {
        super(props);

        this.tambah = this.tambah.bind(this);
        this.toggle = this.toggle.bind(this);
        this.togglePop1 = this.togglePop1.bind(this);
        this.state = {
            popoverOpen1: false,
            activeTab: '2',
            modal: false,
            startDate: new Date(),
          
        }
        this.handleChange = this.handleChange.bind(this);

    }

    togglePop1() {
        this.setState({
            popoverOpen1: !this.state.popoverOpen1
        });
    }

    toggle(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }

    handleChange(date) {
        this.setState({
            startDate: date
        });
    }

    

    render() {

        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                    <Card tabs="true" className="mb-3">
                        <TabContent activeTab={this.state.activeTab}>
                            <TabPane tabId="2">
                                <CardBody>
                                    <div className="widget-chart-wrapper widget-chart-wrapper-lg opacity-10 m-0">
                                        <ResponsiveContainer width="100%" height="100%">
                                            <Col sm="12" md="12" xl="12" style={{ paddingBottom: '50%' }}>
                                                <FormGroup row>
                                                    <Label for="Access" sm={12} size="lg">1. <font>New Account Owner</font></Label>
                                                    <Col sm={4} style={{ paddingLeft: '56px', zIndex: '2' }}>

                                                        <CustomInput type="select"
                                                            name="Access" onClick={this.open}>
                                                            <option value={data} selected disabled hidden>{data ? data : "Select Employee"}</option>
                                                        </CustomInput>


                                                    </Col>
                                                </FormGroup>
                                                <FormGroup row style={{ marginTop: "-311px" }}>
                                                    <Label for="Access" sm={12} size="lg">2. <font style={{ paddingLeft: '20px' }}>Masih pertanyaan</font></Label>
                                                    <Col sm={4} style={{ paddingLeft: '56px' }}>
                                                        <CustomInput type="select"
                                                            name="Access" />


                                                    </Col>
                                                </FormGroup>
                                            </Col>
                                        </ResponsiveContainer>
                                    </div>

                                </CardBody>
                                <CardFooter className="d-block p-4 text-right">
                                    <Button style={{ right: '10px', backgroundColor: "#da222200", color: "black", borderColor: "#ff000000" }} className="fsize-1" size="lg">
                                        Cancel
                                    </Button>
                                    <Button style={{ backgroundColor: '#295950' }} className="btn-wide fsize-1" size="lg">
                                        Save
                                    </Button>
                                </CardFooter>
                            </TabPane>

                        </TabContent>
                    </Card>

                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}



export default MinimalDashboard4;
