import React, { Fragment } from 'react'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

import {
    Row, Col,
    Card, CardBody,Button,ButtonGroup,
    CustomInput
} from 'reactstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import ReactTable from "react-table";

const Designation = [
    { name: 'CEO', id: 7, isChecked: false },
    { name: 'Teknikal Lead', id: 8, isChecked: false },
    { name: 'Scrum Master', id: 9, isChecked: false },
    { name: 'FronEnd Lead Programmer', id: 10, isChecked: false },
    { name: 'BackEnd Programer', id: 2, isChecked: false },
    { name: 'Intership', id: 1, isChecked: false },
    { name: 'Android Programer', id: 3, isChecked: false },
    { name: 'Technical Writter', id: 4, isChecked: false },
]

export default class DataTableFixedHeader extends React.Component {
    constructor() {
        super();
        this.state = {
            desig:Designation,
            checkbox:[],
        };
        this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
        this.renderEditable = this.renderEditable.bind(this);
    }

    renderEditable(cellInfo) {
        return (
            <div
                style={{ backgroundColor: "#fafafa" }}
                contentEditable
                suppressContentEditableWarning
                onBlur={e => {
                    const desig = [...this.state.desig];
                    desig[cellInfo.index][cellInfo.column.id] = e.target.innerHTML;
                    this.setState({ desig });
                }}
                dangerouslySetInnerHTML={{
                    __html: this.state.desig[cellInfo.index][cellInfo.column.id]
                }}
            />
            );
    }
    
    onRadioBtnClick(rSelected) {
        this.setState({ rSelected });
    }

    cek(value){
        this.setState({
            checkbox: value
        })
        console.log(this.state.checkbox)
    }
    
    render() {
        const { desig } = this.state;
        
        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                    <Row>
                        <Col md="12">
                            <Card className="main-card mb-3">
                                <CardBody>
                                    <div className="table-responsive">
                                        <div style={{ paddingBottom:'10px' }}>
                                            <Button className="btn btn-success">
                                                Tambah Data
                                            </Button>
                                        </div>
                                        <BootstrapTable
                                            bootstrap4
                                            keyField="id"
                                            data={desig}
                                            columns={[
                                                {
                                                    sort: true,
                                                    dataField:'id',
                                                    formatter: row => (
                                                
                                                            <div className="d-block w-100 text-center">
                                                                <CustomInput type="checkbox" id={row} onChange={() => this.cek(row)} />
                                                            </div>
                                                        
                                                )},
                                                {
                                                    dataField: 'id',
                                                    text: 'ID',
                                                    sort: true
                                                },
                                                {
                                                    dataField: 'name',
                                                    text: 'Name Job',
                                                    sort: true,
                                                    
                                                },
                                                {
                                                    dataField: 'status',
                                                    isDummyField: false,
                                                    align: 'center',
                                                    text: 'Status',
                                                    formatter: (cellContent, row) => {
                                                        return (
                                                            <div className="d-block w-100 text-center">
                                                                <span className="badge badge-success"> Completed</span>
                                                            </div>
                                                    );
                                                  }
                                                },
                                            ]}
                                        />
                                        <div>
                                            <ButtonGroup size="sm" className="mb-2">
                                                <Button className="btn-dashed" color="success"
                                                    onClick={() => this.onRadioBtnClick(1)}
                                                    active={this.state.rSelected === 1}>One</Button>
                                                <Button className="btn-dashed" color="success"
                                                    onClick={() => this.onRadioBtnClick(2)}
                                                    active={this.state.rSelected === 2}>Two</Button>
                                                <Button className="btn-dashed" color="success"
                                                    onClick={() => this.onRadioBtnClick(3)}
                                                    active={this.state.rSelected === 3}>Three</Button>
                                            </ButtonGroup>
                                        </div>
                                    </div>

                                </CardBody>
                            </Card>
                        </Col>
                        <Col md="12">
                            <Card className="main-card mb-3">
                                <CardBody>
                                    <ReactTable
                                        data={desig}
                                        
                                        SubComponent={row => {
                                            return (
                                                <div style={{ padding: "20px" }}>
                                                    <br />
                                                    <ul>
                                                        
                                                    </ul>
                                                    <br />
                                                </div>
                                            );
                                        }}
                                        
                                        columns={[
                                            {
                                                Header: "Name",
                                                columns: [
                                                    {
                                                        Header: "First Name",
                                                        accessor:'name',
                                                        Cell: this.renderEditable
                                                       
                                                    },
                                                    {
                                                        Header: "Last Name",
                                                        accessor:'id'
                                                       
                                                    }
                                                ]
                                            },
                                            {
                                                Header: "Info",
                                                columns: [
                                                    {
                                                        Header: "Age",
                                                        filterable:false,
                                                        Cell: row => (
                                                            <div className="d-block w-100 text-center">
                                                                    <Button style={{background:'red'}}>cek</Button>
                                                            </div>
                                                        )
                                                      
                                                    }
                                                ]
                                            }
                                        ]}
                                        filterable
                                        style={{ height: "400px" }}
                                        defaultPageSize={20}
                                        />
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}
