import React, { Fragment } from 'react'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

import {
    Row, Col,
    Card, CardBody, Button, ButtonGroup,
    CustomInput, UncontrolledButtonDropdown, DropdownItem, DropdownMenu, DropdownToggle
} from 'reactstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import ReactTable from "react-table";
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory, { PaginationProvider, SizePerPageDropdownStandalone }  from 'react-bootstrap-table2-paginator';


const Designation = [
    { name: 'CEO', id_t: 7, id: 7, jumlah: 100, isChecked: false },
    { name: 'Teknikal Lead', id_t: 8, id: 8, jumlah: 40, isChecked: false },
    { name: 'Scrum Master', id_t: 9, id: 9, jumlah: 30, isChecked: false },
    { name: 'FronEnd Lead Programmer', id_t: 10, id: 10, jumlah: 100, isChecked: false },
    { name: 'BackEnd Programer', id_t: 2, id: 2, jumlah: 50, isChecked: false },
    { name: 'Intership', id_t: 1, id: 1, jumlah: 100, isChecked: false },
    { name: 'Android Programer', id_t: 3, id: 3, jumlah: 80, isChecked: false },
    { name: 'Technical Writter', id_t: 4, id: 4, jumlah: 100, isChecked: false },
    { name: 'deni', id_t: 5, id: 5, jumlah: 100, isChecked: false },



]

const defaultSorted = [{
    dataField: 'name',
    order: 'desc'
    
}]; 



const pageButtonRenderer = ({
  page,
  active,
  disable,
  title,
  onPageChange
}) => {
  const handleClick = (e) => {
    e.preventDefault();
    onPageChange(page);
  };
  const activeStyle = {};
  if (active) {
    activeStyle.backgroundColor = 'black';
    activeStyle.color = 'white';
  } else {
    activeStyle.backgroundColor = 'gray';
    activeStyle.color = 'black';
  }
  if (typeof page === 'string') {
    activeStyle.backgroundColor = 'white';
    activeStyle.color = 'black';
  }
  return (
    <li className="page-item">
      <a href="#" onClick={ handleClick } style={ activeStyle }>{ page }</a>
    </li>
  );
};



const options = {
    custom: true,
    // totalSize: Jabatan.length,
    paginationSize: 'hidden',
    pageStartIndex: 1,
    alwaysShowAllBtns: true, // Always show next and previous button
    // withFirstAndLast: true, // Hide the going to First and Last page button
    // hideSizePerPage: true, // Hide the sizePerPage dropdown always
    // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
    firstPageText: 'First',
    prePageText: 'Back',
    nextPageText: 'Next',
    lastPageText: 'Last',
    // nextPageTitle: 'First page',
    // prePageTitle: 'Pre page',
    // firstPageTitle: 'Next page',
    // lastPageTitle: 'Last page',
    // showTotal: true,
    // paginationTotalRenderer: customTotal,
    // pageButtonRenderer
};

export default class DataTableFixedHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            desig: Designation,
            checkbox: [],
        };
        this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
        this.renderEditable = this.renderEditable.bind(this);
    }

    renderEditable(cellInfo) {
        return (
            <div
                style={{ backgroundColor: "#fafafa" }}
                contentEditable
                suppressContentEditableWarning
                onBlur={e => {
                    const desig = [...this.state.desig];
                    desig[cellInfo.index][cellInfo.column.id] = e.target.innerHTML;
                    this.setState({ desig });
                }}
                dangerouslySetInnerHTML={{
                    __html: this.state.desig[cellInfo.index][cellInfo.column.id]
                }}
            />
        );
    }

    onRadioBtnClick(rSelected,value) {
        this.setState({ rSelected });
        console.log(value)
    }

    cek(value) {
        this.setState({
            checkbox: this.state.checkbox.concat(value)
        })
        console.log(this.state.checkbox)
    }

    oke(value){
        console.log(value)
    }



    // onst options = {
    //     custom: true,
    //     paginationSize: 'hidden',
    //     pageStartIndex: 1,
    //     alwaysShowAllBtns: true,
    //     firstPageText: 'First',
    //     prePageText: 'Back',
    //     nextPageText: 'Next',
    //     lastPageText: 'Last',
        
    // };



    render() {
        const { desig } = this.state;

        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                    <Row>

                        <Col md="12">
                            <Card className="main-card mb-3">
                                <CardBody>
                                    <div style={{ paddingBottom: '10px' }}>
                                        <Button className="btn-dashed" color="primary">
                                            Tambah Data
                                            </Button>
                                    </div>  
                                    <PaginationProvider
                                        pagination={paginationFactory(options)}
                                     >
                                        {
                                            ({
                                                paginationProps,
                                                paginationTableProps
                                            }) => (
                                    <div className="table-responsive">
                                        <SizePerPageDropdownStandalone
                                                            {...paginationProps}  />
                                      
                                        <BootstrapTable
                                            bootstrap4
                                            keyField="id"
                                            data={desig}
                                            columns={[
                                                {
                                                    dataField: 'id',
                                                    text: 'Product ID',
                                                    sort: true
                                                },
                                                {
                                                    dataField: 'name',
                                                    text: 'Product Name',
                                                    sort: true,
                                                    filter: textFilter()
                                                },
                                                {
                                                    dataField: 'orderid',
                                                    text: 'Order ID',
                                                    sort: true,
                                                    align: 'center',

                                                },
                                                {
                                                    dataField: 'status',
                                                    isDummyField: false,
                                                    align: 'center',
                                                    text: 'Status',
                                                    formatter: (cellContent, row) => {
                                                        return (
                                                            <div className="d-block w-100 text-center">
                                                                <span className="badge badge-success"> Completed</span>
                                                            </div>
                                                        );
                                                    }
                                                },
                                                {
                                                    dataField: 'actions',
                                                    isDummyField: true,
                                                    align: 'center',
                                                    text: 'Actions',
                                                    formatter: (cellContent, row) => {
                                                        return (
                                                            <div>
                                                                <div className="d-block w-100 text-center">
                                                                    <UncontrolledButtonDropdown>
                                                                        <DropdownToggle caret className="btn-icon btn-icon-only btn btn-link" color="link">
                                                                            <i className="lnr-menu-circle btn-icon-wrapper" />
                                                                        </DropdownToggle>
                                                                        <DropdownMenu right className="rm-pointers dropdown-menu-hover-link">
                                                                            <DropdownItem header>Header</DropdownItem>
                                                                            <DropdownItem>
                                                                                <i className="dropdown-icon lnr-inbox"> </i>
                                                                                <span>Menus</span>
                                                                            </DropdownItem>
                                                                            <DropdownItem>
                                                                                <i className="dropdown-icon lnr-file-empty"> </i>
                                                                                <span>Settings</span>
                                                                            </DropdownItem>
                                                                            <DropdownItem>
                                                                                <i className="dropdown-icon lnr-book"> </i>
                                                                                <span>Actions</span>
                                                                            </DropdownItem>
                                                                            <DropdownItem divider />
                                                                            <DropdownItem>
                                                                                <i className="dropdown-icon lnr-picture"> </i>
                                                                                <span>Dividers</span>
                                                                            </DropdownItem>
                                                                        </DropdownMenu>
                                                                    </UncontrolledButtonDropdown>
                                                                </div>
                                                            </div>
                                                        );
                                                    }
                                                },
                                            ]}
                                            filter={filterFactory()}
                                            defaultSorted={defaultSorted}
                                            striped={true}
                                            bordered={false}
                                            {...paginationTableProps}/>
                                    </div>
                                    )
                                        }
                                        </PaginationProvider> 

                                    <PaginationProvider
                                        pagination={paginationFactory(options)}
                                    >
                                        {
                                            ({
                                                paginationProps,
                                                paginationTableProps
                                            }) => (

                                                    <div className="table-responsive" style={{ overflow: 'hidden', maxHeight: '2000px' }}>
                                                        <BootstrapTable
                                                            bootstrap4
                                                            keyField="id"
                                                            data={data}
                                                            columns={columns1}
                                                            filter={filterFactory()}
                                                            defaultSorted={defaultSorted}
                                                            // selectRow={selectRow}
                                                            cellEdit={cellEditFactory({ mode: 'click' })}
                                                            {...paginationTableProps}
                                                        />
                                                        <PaginationListStandalone
                                                            {...paginationProps}
                                                        />
                                                    </div>
                                                )
                                        }
                                    </PaginationProvider>





                                          
                                {/* <div>
                                    <ButtonGroup size="lg" className="mb-2">
                                        <Button className="btn-dashed" color="primary"
                                            onClick={() => this.onRadioBtnClick(1, 'dandi')}
                                            active={this.state.rSelected === 1}>First</Button>
                                        <Button className="btn-dashed" color="primary"
                                            onClick={() => this.onRadioBtnClick(2, 'deni')}
                                            active={this.state.rSelected === 2}>Next</Button>
                                        <Button className="btn-dashed" color="primary"
                                            onClick={() => this.onRadioBtnClick(3, 'oke')}
                                            active={this.state.rSelected === 3}>Previous</Button>
                                        <Button className="btn-dashed" color="primary"
                                            onClick={() => this.onRadioBtnClick(4, 'mantap')}
                                            active={this.state.rSelected === 4}>last</Button>
                                    </ButtonGroup>
                                </div> */}
                                </CardBody>
                            </Card>
                        </Col>


                        <Col md="12">
                            <Card className="main-card mb-3">
                                <CardBody>
                                    <div style={{ paddingBottom: '10px' }}>
                                        <Button className="btn-dashed" color="primary">
                                            Tambah Data
                                            </Button>
                                    </div>
                                    <ReactTable
                                        data={desig}
                                        
                                        columns={[
                                            {
                                                Header: "Name",
                                                columns: [
                                                    {
                                                        Header: "Check",
                                                        filterable:false,
                                                        width:100,
                                                        Cell: row => { 
                                                            return (
                                                                <div className="d-block w-100 text-center">
                                                                    <CustomInput type="checkbox" id={row.original.id_t} />
                                                                </div>
                                                            );
                                                    }
                                                    },
                                                    {
                                                        Header: "First Name",
                                                        accessor: 'name',
                                                        Cell: this.renderEditable

                                                    },
                                                    {
                                                        Header: "Last Name",
                                                        accessor: 'jumlah'

                                                    }
                                                ]
                                            },
                                            {
                                                Header: "Info",
                                                columns: [{
                                                    Header: "Age",
                                                    filterable: false,
                                                    accessor: 'jumlah',
                                                    Cell: row => {
                                                        if (row.original.jumlah >= 50) {
                                                            return (
                                                                <div className="d-block w-100 text-center">
                                                                    <span className="btn btn-success">Completed</span>
                                                                </div>
                                                            );
                                                        } else {
                                                            return (
                                                                <div className="d-block w-100 text-center">
                                                                    <span onClick={()=> this.oke(row.original.id)} className="btn btn-danger">Uncompleted</span>
                                                                </div>
                                                            );
                                                        }
                                                    }
                                                }]
                                            }
                                        ]}
                                        filterable
                                       pageSize={5}
                                        showPageSizeOptions={false}
                                        showPageJump={false}
                                        showPaginationBottom={false}
                                    />
                                    <div style={{ paddingTop: '10px' }}>
                                        <ButtonGroup size="lg" className="mb-2">
                                            <Button className="btn-dashed" color="primary"
                                                onClick={() => this.onRadioBtnClick(1,'dandi')}
                                                active={this.state.rSelected === 1}>First</Button>
                                            <Button className="btn-dashed" color="primary"
                                                onClick={() => this.onRadioBtnClick(2, 'deni')}
                                                active={this.state.rSelected === 2}>Next</Button>
                                            <Button className="btn-dashed" color="primary"
                                                onClick={() => this.onRadioBtnClick(3, 'oke')}
                                                active={this.state.rSelected === 3}>Previous</Button>
                                            <Button className="btn-dashed" color="primary"
                                                onClick={() => this.onRadioBtnClick(4, 'mantap')}
                                                active={this.state.rSelected === 4}>last</Button>
                                        </ButtonGroup>
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}
