import React, { Fragment } from 'react'
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

import {
    Row, Col,
    Card, CardBody,
} from 'reactstrap';

import ReactTable from "react-table";

const Designation = [
    { name: 'CEO', id: 7, isChecked: false },
    { name: 'Teknikal Lead', id: 8, isChecked: false },
    { name: 'Scrum Master', id: 9, isChecked: false },
    { name: 'FronEnd Lead Programmer', id: 10, isChecked: false },
    { name: 'BackEnd Programer', id: 2, isChecked: false },
    { name: 'Intership', id: 1, isChecked: false },
    { name: 'Android Programer', id: 3, isChecked: false },
    { name: 'Technical Writter', id: 4, isChecked: false },
]

export default class DataTableFixedHeader extends React.Component {
    constructor() {
        super();
        this.state = {
            desig:Designation,
        };
    }



    renderEditable(cellInfo) {
        return (
            <div
                style={{ backgroundColor: "#fafafa" }}
                contentEditable
                suppressContentEditableWarning
                onBlur={e => {
                    const data = [...this.state.data];
                    data[cellInfo.index][cellInfo.column.id] = e.target.innerHTML;
                    this.setState({ data });
                }}
                dangerouslySetInnerHTML={{
                    __html: this.state.data[cellInfo.index][cellInfo.column.id]
                }}
            />
        );
    }

    render() {
        const { desig } = this.state;
        

        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                    <Row>
                        <Col md="12">
                            <Card className="main-card mb-3">
                                <CardBody>
                                    <ReactTable
                                        data={desig}
                                        columns={[
                                            {
                                                Header: "Name",
                                                columns: [
                                                    {
                                                        Header: "First Name",
                                                        accessor:'name',
                                                        Cell: this.renderEditable
                                                       
                                                    },
                                                    {
                                                        Header: "Last Name",
                                                        accessor:'id'
                                                       
                                                    }
                                                ]
                                            },
                                            {
                                                Header: "Info",
                                                columns: [
                                                    {
                                                        Header: "Age",
                                                        filterable:false
                                                      
                                                    }
                                                ]
                                            }
                                        ]}
                                        filterable
                                        SubComponent={row => {
                                            return (
                                                <div style={{ padding: "20px" }}>
                                                    <br />
                                                    <ul>
                                                        
                                                    </ul>
                                                    <br />
                                                </div>
                                            );
                                        }}
                                        style={{ height: "400px" }}
                                        defaultPageSize={20}
                                        />
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}
