import React, { Component, Fragment } from 'react';

import ReactCSSTransitionGroup from 'react-addons-css-transition-group';


import IncomeReport from '../../Commerce/Examples/Components/IncomeReport';
import IncomeReport2 from '../../Commerce/Examples/Components/IncomeReport2';


import classnames from 'classnames';


import {
    faCalendarAlt,

} from '@fortawesome/free-solid-svg-icons';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import DatePicker from 'react-datepicker';



import {
    InputGroup, InputGroupAddon,
    Col, Nav,
    Button,
    CardHeader,
    NavItem,
    NavLink,
    TabContent,
    TabPane,
    Card,
    CardBody, Input, CardFooter,
    Form, FormGroup, Label, CustomInput,
    Modal, ModalHeader, ModalBody, ModalFooter

} from 'reactstrap';


import ReactTable from "react-table";

import {
    ResponsiveContainer,

} from 'recharts';




export default class MinimalDashboard2 extends Component {

    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            startDate: new Date()

        };

        this.tambah = this.tambah.bind(this);
        this.toggle = this.toggle.bind(this);
        this.togglePop1 = this.togglePop1.bind(this);
        this.state = {
            popoverOpen1: false,
            activeTab: '2',
        }
        this.handleChange = this.handleChange.bind(this);
    }

    togglePop1() {
        this.setState({
            popoverOpen1: !this.state.popoverOpen1
        });
    }

    toggle(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }

    tambah() {
        this.setState({
            modal: !this.state.modal
        });
    }


    handleChange(date) {
        this.setState({
            startDate: date
        });
    }



    render() {

        return (
            <Fragment>
                <ReactCSSTransitionGroup
                    component="div"
                    transitionName="TabsAnimation"
                    transitionAppear={true}
                    transitionAppearTimeout={0}
                    transitionEnter={false}
                    transitionLeave={false}>
                  
                    <Card tabs="true" className="mb-3">
                        <CardHeader className="tabs-lg-alternate">
                            <Nav justified>
                                <NavItem>
                                    <NavLink href="javascript:void(0);" className={classnames({ active: this.state.activeTab === '1' })}
                                        onClick={() => {
                                            this.toggle('1');
                                        }}
                                    >
                                        <h1><i className="pe-7s-users" style={{ fontSize: '80px' }} ></i></h1>
                                        <div className="tab-subheading">
                                            <span className="pr-2 opacity-6">
                                                Data Guru
                                            </span>
                                        </div>
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="javascript:void(0);"
                                        className={classnames({ active: this.state.activeTab === '2' })}
                                        onClick={() => {
                                            this.toggle('2');
                                        }}
                                    >
                                        <h1><i className="pe-7s-study" style={{ fontSize: '80px' }} ></i></h1>
                                        <div className="tab-subheading">
                                            <span className="pr-2 opacity-6">
                                                Data Siswa
                                            </span>
                                        </div>
                                    </NavLink>
                                </NavItem>

                                <NavItem>
                                    <NavLink href="javascript:void(0);"
                                        className={classnames({ active: this.state.activeTab === '3' })}
                                        onClick={() => {
                                            this.toggle('3');
                                        }}
                                    >
                                        <h1><i className="pe-7s-bookmarks" style={{ fontSize: '80px' }} ></i></h1>
                                        <div className="tab-subheading">
                                            <span className="pr-2 opacity-6">
                                                Data Pelajaran
                                            </span>
                                        </div>
                                    </NavLink>
                                </NavItem>
                            </Nav>
                        </CardHeader>

                        <TabContent activeTab={this.state.activeTab}>
                           
                            <TabPane tabId="2">
                                <CardBody>
                                    <div className="widget-chart-wrapper widget-chart-wrapper-lg opacity-10 m-0">
                                        <ResponsiveContainer width="100%" height="100%">
                                            <Col sm="12" md="12" xl="12">
                                                <Card className="mb-3">
                                                   
                                                    <CardBody>
                                                    </CardBody>
                                                </Card>
                                            </Col>
                                        </ResponsiveContainer>
                                    </div>
                                </CardBody>
                            </TabPane>
                            
                        </TabContent>
                    </Card>

                </ReactCSSTransitionGroup>
            </Fragment>
        )
    }
}
